      days_ = None
      days_r = None
      if days:
        q = 24*60*60
        days_ = int(x/q)
        days_r = x%q
        x = days_r
      hours_ = None
      hours_r = None
      if hours:
        q = 60*60
        hours_ = int(x/q)
        hours_r = x%q
        x = hours_r
      minutes_ = None
      minutes_r = None
      if minutes:
        q = 60
        minutes_ = int(x/q)
        minutes_r = x%q
        x = minutes_r
      seconds_ = None
      seconds_r = None
      if seconds:
        q = 1
        seconds_ = int(x/q)
        seconds_r = x%q
        x = seconds_r
      r = ""
      if days:
        r += "%s Days"%(days_)
        r += ", "
      if hours:
        r += "%s Hours"%(hours_)
        r += ", "
      if minutes:
        r += "%s Minutes"%(minutes_)
        r += ", "
