      print("youtube subtitle downloading url: %s" % url)
      def dl(url):
        x = getoutput("youtube-dl --sub-lang en --sub-format vtt --write-auto-sub --skip-download %s" % url)
        x = x.split(": ")[-1]
        getoutput("mv '%s' '%s'" % (x, x.replace(" ", "_")))
