      def to_txt(fn):
        with open(fn, "r") as f:
          txt = f.read()
        # <>s
        pattern = r'(<.*?>)'
        txt = re.sub(pattern, "", txt)
        # timestamps
        pattern = r'(.*-->.*)'
        txt = re.sub(pattern, "", txt)
        txt = txt.split("\n")
        load = []
        for i in txt:
          i = i.strip()
          load.append(i)
        txt = "\n".join(load)
        for i in range(20):
          txt = txt.replace("\n\n", "\n")

        txt = txt.split("\n")
        load = []
        # Only append if line is not duplicate.
        for idx, i in enumerate(txt[:-1]):
          try:
            if txt[idx] != txt[idx+1]:
              load.append(i)
          except Exception as e:
            print(e)
        txt = "\n".join(load)
        return txt
      def lineskip(txt):
        lineskips = [" but", " um", " I'm", " I"]
        txt = txt.replace("\n", " ")
        for i in lineskips:
          txt = txt.replace(i, "\n%s"%i)
        return txt
      def main(url):
        fn = dl(url)
        txt = to_txt(fn)
        txt = lineskip(txt)
        with open("%s.txt"%fn, "w") as f:
          f.write(txt)
      main(url)
      print("success")


